mapper.select:
select ,
ce.name carbonExchangeName, cmp.project_introduction, cml.name carbonMethodologyName, cml.certification_criteria, cmr.project_status_code, cmr.project_name, cmr.project_scope_code, cmr.project_industry_code, cmr.project_scope_type_code, cmr.project_scope_type from carbon_credit_assets cca left join carbon_exchange ce on ce.id = cca.carbon_exchange_id left join carbon_metaregistry_project cmp on cmp.metaregistry_id = cca.carbon_project_id left join carbon_metaregistry cmr on cmr.id = cmp.metaregistry_id left join carbon_methodology cml on cml.dict_code = cmr.methodology_code
and cmr.project_name like CONCAT('%',#{param.projectName},'%')
and cml.name like CONCAT('%',#{param.methodName},'%')
and cml.certification_criteria = #{param.certificationCriteria}
and cca.assets_status = #{param.assetsStatus}
and cca.transaction_status = #{param.transactionStatus}
and cmr.project_scope_type_code = #{param.industryCode}
and cca.issuing_date >= #{param.issuingDateStart}
and cca.issuing_date <= #{param.issuingDateEnd}
and cmr.project_scope_code = #{param.projectScopeCode}
